using UnityEditor;
using Unity.Scenes;
using UnityEngine;

public class SetSubScene
{
    public static void Execute()
    {
        var go = GameObject.Find("RockSubScene");
        if (go == null)
        {
            Debug.LogError("RockSubScene not found");
            return;
        }

        var subScene = go.GetComponent<SubScene>();
        if (subScene == null)
        {
            Debug.LogError("SubScene component not found");
            return;
        }

        var sceneAsset = AssetDatabase.LoadAssetAtPath<SceneAsset>("Assets/Scenes/Godgame_RockSubScene.unity");
        if (sceneAsset == null)
        {
            Debug.LogError("SceneAsset not found");
            return;
        }

        subScene.SceneAsset = sceneAsset;
        Debug.Log("Set SubScene asset successfully");
    }
}

