        var subScene = anchor.GetComponent<SubScene>();
        if (subScene == null)
        {
            subScene = anchor.AddComponent<SubScene>();
        }

        var sceneAsset = AssetDatabase.LoadAssetAtPath<SceneAsset>(subScenePath);
        if (sceneAsset == null)
