        subScene.AutoLoadScene = true;
        EditorUtility.SetDirty(subScene);
    }

    private static T LoadOrCreate<T>(string path, string assetName) where T : ScriptableObject
    {
        var asset = AssetDatabase.LoadAssetAtPath<T>(path);
        if (asset != null)
