                if (UnityEngine.Time.frameCount % 120 == 0) // Every 2 seconds at 60fps
                {
                    Debug.Log($"[GodgameCamera] Pos: {_position:F1}, Yaw: {_yaw:F1}°, Pitch: {_pitch:F1}°");
                }
#endif
            }
            else
