                // Debug logging for zoom
                if (UnityEngine.Time.frameCount % 30 == 0) // Log every half second
                {
                    Debug.Log($"[GodgameCamera] Zoom: {zoomAmount:F2}, Hit: {hit:F1}, NewPos: {_position:F1}");
                }
#endif
            }
            else
            {
