            float scale = _panSpeedPerPixel; // no dt here: delta is per-frame already
            Vector3 pan =
                (-right * delta.x +   // dragging mouse right moves camera left
                 -forward * delta.y)  // dragging mouse up moves camera forward/back
                * scale;

            _position += pan;

