using UnityEngine;
using UnityEditor;
using UnityEditor.SceneManagement;
using Godgame.Demo;

public class FixDemoConfigReference
{
    [MenuItem("Tools/Fix Demo Config Reference")]
    public static void Fix()
    {
        string scenePath = "Assets/Scenes/DemoConfigSubScene.unity";
        var scene = EditorSceneManager.OpenScene(scenePath, OpenSceneMode.Single);

        var rootObjects = scene.GetRootGameObjects();
        foreach (var root in rootObjects)
        {
            var authoring = root.GetComponentInChildren<DemoSettlementAuthoring>();
            if (authoring != null)
            {
                Debug.Log($"Found DemoSettlementAuthoring on {authoring.gameObject.name}");

                string prefabPath = "Assets/Prefabs/Settlement/VillageCenter.prefab";
                var prefab = AssetDatabase.LoadAssetAtPath<GameObject>(prefabPath);

                if (prefab != null)
                {
                    authoring.villageCenterPrefab = prefab;
                    Debug.Log($"Reassigned villageCenterPrefab to {prefab.name}");
                    EditorUtility.SetDirty(authoring);
                    EditorSceneManager.MarkSceneDirty(scene);
                    EditorSceneManager.SaveScene(scene);
                    Debug.Log("Scene saved.");
                }
                else
                {
                    Debug.LogError($"Could not load prefab at {prefabPath}");
                }
                return;
            }
        }

        Debug.LogError("Could not find DemoSettlementAuthoring in the scene.");
    }
}

using UnityEngine;
using UnityEditor;
using UnityEditor.SceneManagement;
using Godgame.Demo;

public class CheckGodgameScene
{
    [MenuItem("Tools/Check Godgame Scene")]
    public static void Check()
    {
        string scenePath = "Assets/Godgame.unity";
        var scene = EditorSceneManager.OpenScene(scenePath, OpenSceneMode.Single);

        var rootObjects = scene.GetRootGameObjects();
        foreach (var root in rootObjects)
        {
            var authoring = root.GetComponentInChildren<DemoSettlementAuthoring>();
            if (authoring != null)
            {
                Debug.Log($"Found DemoSettlementAuthoring on {authoring.gameObject.name}");
                if (authoring.villageCenterPrefab != null)
                {
                    Debug.Log($"villageCenterPrefab is assigned: {authoring.villageCenterPrefab.name} (AssetPath: {AssetDatabase.GetAssetPath(authoring.villageCenterPrefab)})");
                }
                else
                {
                    Debug.LogError("villageCenterPrefab is NULL");
                }
                return;
            }
        }
        Debug.LogError("Could not find DemoSettlementAuthoring in Godgame.unity");
    }
}

