                    ResourceTypeTint = new float4(1f, 1f, 1f, 1f),
                    QuantityScale = 0.5f + resource.ValueRO.Quantity * 0.01f,
                    IsCarried = 0
                });
                anyWork = true;
            }

            if (anyWork)
            {
                ecb.Playback(state.EntityManager);
            }
            ecb.Dispose();
        }

