using UnityEngine;
using UnityEditor;
using UnityEditor.SceneManagement;
using Godgame.Rendering;

public class FixRenderCatalog
{
    [MenuItem("Tools/Fix Render Catalog")]
    public static void Fix()
    {
        string scenePath = "Assets/Scenes/TRI_Godgame_Smoke.unity";
        var scene = EditorSceneManager.OpenScene(scenePath, OpenSceneMode.Single);

        var rootObjects = scene.GetRootGameObjects();
        GameObject catalogGO = null;
        foreach (var root in rootObjects)
        {
            if (root.name == "GodgameRenderCatalog")
            {
                catalogGO = root;
                break;
            }
        }

        if (catalogGO != null)
        {
            Debug.Log("Found GodgameRenderCatalog in main scene.");
            // Check if it has RenderCatalogAuthoring
            var authoring = catalogGO.GetComponent<RenderCatalogAuthoring>();
            if (authoring != null)
            {
                Debug.Log("RenderCatalogAuthoring is present.");
                // To ensure it gets baked, we can try to make it a subscene or ensure the main scene is baked.
                // However, typically singletons like this are put in a "Registry" subscene.
                // Let's check if GodgameRegistry subscene already has it.

                string registryScenePath = "Assets/_Archive/Scenes/Godgame_OrbitDebug.unity"; // Based on GodgameRegistry subscene asset path
                // Wait, GodgameRegistry GameObject in TRI_Godgame_Smoke has a SubScene component pointing to Assets/_Archive/Scenes/Godgame_OrbitDebug.unity
                // Let's check if that scene has a RenderCatalog.

                var registryScene = EditorSceneManager.OpenScene(registryScenePath, OpenSceneMode.Additive);
                bool foundInRegistry = false;
                foreach (var root in registryScene.GetRootGameObjects())
                {
                    if (root.GetComponent<RenderCatalogAuthoring>() != null)
                    {
                        foundInRegistry = true;
                        Debug.Log("Found RenderCatalogAuthoring in GodgameRegistry subscene.");
                        break;
                    }
                }

                if (foundInRegistry)
                {
                    Debug.Log("RenderCatalogAuthoring exists in GodgameRegistry. Deleting duplicate from main scene.");
                    Object.DestroyImmediate(catalogGO);
                    EditorSceneManager.MarkSceneDirty(scene);
                    EditorSceneManager.SaveScene(scene);
                }
                else
                {
                    Debug.Log("RenderCatalogAuthoring NOT found in GodgameRegistry. Moving it there.");
                    EditorSceneManager.MoveGameObjectToScene(catalogGO, registryScene);
                    EditorSceneManager.MarkSceneDirty(scene);
                    EditorSceneManager.MarkSceneDirty(registryScene);
                    EditorSceneManager.SaveScene(scene);
                    EditorSceneManager.SaveScene(registryScene);
                }

                EditorSceneManager.CloseScene(registryScene, true);
            }
        }
        else
        {
            Debug.Log("GodgameRenderCatalog NOT found in main scene. Checking registry...");
             string registryScenePath = "Assets/_Archive/Scenes/Godgame_OrbitDebug.unity";
             var registryScene = EditorSceneManager.OpenScene(registryScenePath, OpenSceneMode.Additive);
             bool foundInRegistry = false;
             foreach (var root in registryScene.GetRootGameObjects())
             {
                 if (root.GetComponent<RenderCatalogAuthoring>() != null)
                 {
                     foundInRegistry = true;
                     Debug.Log("Found RenderCatalogAuthoring in GodgameRegistry subscene.");
                     break;
                 }
             }
             if (!foundInRegistry)
             {
                 Debug.LogError("RenderCatalogAuthoring missing from both main scene and registry!");
             }
             EditorSceneManager.CloseScene(registryScene, true);
        }
    }
}

using UnityEngine;
using UnityEditor;
using UnityEditor.SceneManagement;
using Godgame.Demo;

public class FixDemoConfig
{
    [MenuItem("Tools/Fix Demo Config")]
    public static void Fix()
    {
        string scenePath = "Assets/Scenes/DemoConfigSubScene.unity";
        var scene = EditorSceneManager.OpenScene(scenePath, OpenSceneMode.Single);

        var rootObjects = scene.GetRootGameObjects();
        foreach (var root in rootObjects)
        {
            var authoring = root.GetComponentInChildren<DemoSettlementAuthoring>();
            if (authoring != null)
            {
                Debug.Log($"Found DemoSettlementAuthoring on {authoring.gameObject.name}");
                // Force dirty to trigger rebake
                EditorUtility.SetDirty(authoring);
                EditorSceneManager.MarkSceneDirty(scene);
                EditorSceneManager.SaveScene(scene);
                Debug.Log("DemoConfigSubScene saved and marked dirty to trigger rebake.");
                return;
            }
        }
        Debug.LogError("Could not find DemoSettlementAuthoring in DemoConfigSubScene.");
    }
}

