arguments.content:
using UnityEngine;
using UnityEditor;
using UnityEditor.SceneManagement;
using Godgame.Demo;

public class RobustFixDemoConfig
{
    [MenuItem("Tools/Robust Fix Demo Config")]
    public static void Fix()
    {
        string scenePath = "Assets/Scenes/DemoConfigSubScene.unity";
        var scene = EditorSceneManager.OpenScene(scenePath, OpenSceneMode.Single);

        var rootObjects = scene.GetRootGameObjects();
        foreach (var root in rootObjects)
        {
            var authoring = root.GetComponentInChildren<DemoSettlementAuthoring>();
            if (authoring != null)
            {
                Debug.Log($"Found DemoSettlementAuthoring on {authoring.gameObject.name}");

                // Reassign ALL prefabs
                authoring.villageCenterPrefab = AssetDatabase.LoadAssetAtPath<GameObject>("Assets/Prefabs/Settlement/VillageCenter.prefab");
                authoring.storehousePrefab = AssetDatabase.LoadAssetAtPath<GameObject>("Assets/Prefabs/Settlement/Storehouse.prefab");
                authoring.housingPrefab = AssetDatabase.LoadAssetAtPath<GameObject>("Assets/Prefabs/Settlement/Housing.prefab");
                authoring.worshipPrefab = AssetDatabase.LoadAssetAtPath<GameObject>("Assets/Prefabs/Settlement/Worship.prefab");
                authoring.villagerPrefab = AssetDatabase.LoadAssetAtPath<GameObject>("Assets/Prefabs/Villagers/Villager.prefab");

                Debug.Log($"Reassigned villageCenterPrefab to {authoring.villageCenterPrefab?.name}");
                Debug.Log($"Reassigned storehousePrefab to {authoring.storehousePrefab?.name}");
                Debug.Log($"Reassigned housingPrefab to {authoring.housingPrefab?.name}");
                Debug.Log($"Reassigned worshipPrefab to {authoring.worshipPrefab?.name}");
                Debug.Log($"Reassigned villagerPrefab to {authoring.villagerPrefab?.name}");

                EditorUtility.SetDirty(authoring);
                EditorSceneManager.MarkSceneDirty(scene);
                EditorSceneManager.SaveScene(scene);
                Debug.Log("Scene saved.");

                // Force reimport of the scene asset to ensure metadata is updated
                AssetDatabase.ImportAsset(scenePath, ImportAssetOptions.ForceUpdate);
                Debug.Log("Scene asset reimported.");
                return;
            }
        }

        Debug.LogError("Could not find DemoSettlementAuthoring in the scene.");
    }
}

