using UnityEngine;
using UnityEditor;
using UnityEditor.SceneManagement;
using Godgame.Rendering;

public class FixRenderCatalogInMainScene
{
    [MenuItem("Tools/Fix Render Catalog In Main Scene")]
    public static void Fix()
    {
        string scenePath = "Assets/Scenes/TRI_Godgame_Smoke.unity";
        var scene = EditorSceneManager.OpenScene(scenePath, OpenSceneMode.Single);

        // Check if GodgameRenderCatalog exists in the main scene
        GameObject catalogGO = null;
        foreach (var root in scene.GetRootGameObjects())
        {
            if (root.name == "GodgameRenderCatalog")
            {
                catalogGO = root;
                break;
            }
        }

        if (catalogGO == null)
        {
            Debug.Log("GodgameRenderCatalog not found in main scene. Creating it.");
            catalogGO = new GameObject("GodgameRenderCatalog");
            var authoring = catalogGO.AddComponent<RenderCatalogAuthoring>();

            // Assign the catalog definition asset
            var catalogDef = AssetDatabase.LoadAssetAtPath<Godgame.Rendering.Catalog.GodgameRenderCatalogDefinition>("Assets/Rendering/GodgameRenderCatalog.asset");
            if (catalogDef != null)
            {
                authoring.CatalogDefinition = catalogDef;
                Debug.Log("Assigned GodgameRenderCatalog asset to authoring component.");
            }
            else
            {
                Debug.LogError("Could not find GodgameRenderCatalog.asset at Assets/Rendering/GodgameRenderCatalog.asset");
            }
        }
        else
        {
            Debug.Log("GodgameRenderCatalog found in main scene.");
            var authoring = catalogGO.GetComponent<RenderCatalogAuthoring>();
            if (authoring == null)
            {
                authoring = catalogGO.AddComponent<RenderCatalogAuthoring>();
            }

            if (authoring.CatalogDefinition == null)
            {
                 var catalogDef = AssetDatabase.LoadAssetAtPath<Godgame.Rendering.Catalog.GodgameRenderCatalogDefinition>("Assets/Rendering/GodgameRenderCatalog.asset");
                 if (catalogDef != null)
                 {
                     authoring.CatalogDefinition = catalogDef;
                     Debug.Log("Assigned GodgameRenderCatalog asset to authoring component.");
                 }
            }
        }

        // Ensure it is marked as a SubScene or handled correctly for baking.
        // Since the user wants it in TRI_Godgame_Smoke, and that scene is likely baked when entering play mode (if it has authoring components),
        // we just need to ensure it's there.

        // However, if the main scene is NOT a subscene itself, we might need to ensure it's baked.
        // But typically, the active scene is baked.

        EditorUtility.SetDirty(catalogGO);
        EditorSceneManager.MarkSceneDirty(scene);
        EditorSceneManager.SaveScene(scene);
        Debug.Log("TRI_Godgame_Smoke saved with GodgameRenderCatalog.");
    }
}
