using UnityEngine;
using UnityEditor;
using System.Collections.Generic;
using System.IO;

public class CheckMissingScripts
{
    public static void Execute()
    {
        string[] prefabPaths = new string[]
        {
            "Assets/Resources/Prefabs/CameraInputRig.prefab",
            "Assets/Prefabs/Systems/GodgamePresentationRuntimeBootstrap.prefab",
            "Assets/Prefabs/Scenes/GodgameDemoRig.prefab" // Checking this one too just in case
        };

        foreach (string path in prefabPaths)
        {
            GameObject prefab = AssetDatabase.LoadAssetAtPath<GameObject>(path);
            if (prefab != null)
            {
                CheckGameObject(prefab, path);
            }
            else
            {
                Debug.LogWarning($"Prefab not found at path: {path}");
            }
        }

        // Also search for any prefab with "Debug" or "Cube" in the name in Resources
        string[] allPrefabs = Directory.GetFiles("Assets", "*.prefab", SearchOption.AllDirectories);
        foreach (string path in allPrefabs)
        {
            if (path.Contains("Debug") || path.Contains("Cube"))
            {
                 GameObject prefab = AssetDatabase.LoadAssetAtPath<GameObject>(path);
                 if (prefab != null)
                 {
                     CheckGameObject(prefab, path);
                 }
            }
        }
    }

    private static void CheckGameObject(GameObject go, string path)
    {
        Component[] components = go.GetComponentsInChildren<Component>(true);
        foreach (Component c in components)
        {
            if (c == null)
            {
                Debug.LogError($"Missing script found on GameObject '{go.name}' (Child of {path})");
                // We can try to remove it here if we want, but let's just report first.
            }
        }

        // Also check children recursively if GetComponentsInChildren didn't catch everything (it should though)
        // Actually GetComponentsInChildren returns components on the GO and its children.
        // But if the component is null, we can't know which GameObject it was on easily from the array if we don't iterate transforms.

        CheckTransform(go.transform, path);
    }

    private static void CheckTransform(Transform t, string path)
    {
        Component[] components = t.GetComponents<Component>();
        for (int i = 0; i < components.Length; i++)
        {
            if (components[i] == null)
            {
                Debug.LogError($"Missing script found on GameObject '{t.name}' in prefab '{path}' at index {i}");

                // To remove it:
                // GameObjectUtility.RemoveMonoBehavioursWithMissingScript(t.gameObject);
                // But let's just report for now.
            }
        }

        foreach (Transform child in t)
        {
            CheckTransform(child, path);
        }
    }
}

