using UnityEngine;
using System;
using System.Reflection;
using System.Linq;

public class FindType
{
    public static void Execute()
    {
        var typeName = "PureDotsRuntimeConfigLoader";
        var type = AppDomain.CurrentDomain.GetAssemblies()
            .SelectMany(a => a.GetTypes())
            .FirstOrDefault(t => t.Name == typeName);

        if (type != null)
        {
            Debug.Log($"Found type: {type.FullName}");
        }
        else
        {
            Debug.Log($"Type {typeName} not found.");
        }
    }
}

using UnityEngine;
using System;
using System.Reflection;
using System.Linq;

public class FindConfigLoader
{
    public static void Execute()
    {
        var types = AppDomain.CurrentDomain.GetAssemblies()
            .SelectMany(a => a.GetTypes())
            .Where(t => t.Name.Contains("ConfigLoader"))
            .Select(t => t.FullName);

        foreach (var t in types)
        {
            Debug.Log($"Found type: {t}");
        }
    }
}

using UnityEngine;
using System;
using System.Reflection;
using System.Linq;

public class FindPureDotsTypes
{
    public static void Execute()
    {
        var types = AppDomain.CurrentDomain.GetAssemblies()
            .SelectMany(a => a.GetTypes())
            .Where(t => t.Name.Contains("PureDots") && typeof(MonoBehaviour).IsAssignableFrom(t))
            .Select(t => t.FullName);

        foreach (var t in types)
        {
            Debug.Log($"Found type: {t}");
        }
    }
}

