using UnityEditor;
using Unity.Scenes;
using UnityEngine;

public class CloseSubScene
{
    public static void Execute()
    {
        var holder = GameObject.Find("GodgameConfig");
        if (holder == null) return;
        var subScene = holder.GetComponent<SubScene>();
        if (subScene == null) return;

        // Close the scene (unload from hierarchy, keep as subscene)
        if (subScene.IsLoaded)
        {
            EditorSceneManager.CloseScene(subScene.EditingScene, false);
        }
        Debug.Log("Closed subscene");
    }
}

