using UnityEditor;
using UnityEditor.SceneManagement;
using UnityEngine;
using PureDOTS.Authoring;

public class RebakeSubScene
{
    public static void Execute()
    {
        var scenePath = "Assets/Scenes/GodgameConfig.unity";
        var scene = EditorSceneManager.OpenScene(scenePath, OpenSceneMode.Additive);

        var go = GameObject.Find("PureDotsConfigAuthoring");
        if (go != null)
        {
            var comp = go.GetComponent<PureDotsConfigAuthoring>();
            if (comp != null)
            {
                EditorUtility.SetDirty(comp);
                Debug.Log("Dirtied PureDotsConfigAuthoring");
            }
        }

        var go2 = GameObject.Find("SpatialPartitionAuthoring");
        if (go2 != null)
        {
            var comp = go2.GetComponent<SpatialPartitionAuthoring>();
            if (comp != null)
            {
                EditorUtility.SetDirty(comp);
                Debug.Log("Dirtied SpatialPartitionAuthoring");
            }
        }

        EditorSceneManager.SaveScene(scene);
        EditorSceneManager.CloseScene(scene, true);
        Debug.Log("Saved and closed scene");
    }
}

using UnityEngine;
using System;
using System.Reflection;
using System.Linq;

public class FindSpatialPartitionAuthoring
{
    public static void Execute()
    {
        var typeName = "SpatialPartitionAuthoring";
        var type = AppDomain.CurrentDomain.GetAssemblies()
            .SelectMany(a => a.GetTypes())
            .FirstOrDefault(t => t.Name == typeName);

        if (type != null)
        {
            Debug.Log($"Found type: {type.FullName} in {type.Assembly.Location}");
        }
        else
        {
            Debug.Log($"Type {typeName} not found.");
        }
    }
}

using UnityEngine;
using System;
using System.Reflection;
using System.Linq;
using Unity.Entities;

public class InspectSpatialPartitionAuthoring
{
    public static void Execute()
    {
        var typeName = "PureDOTS.Authoring.SpatialPartitionAuthoring";
        var type = AppDomain.CurrentDomain.GetAssemblies()
            .SelectMany(a => a.GetTypes())
            .FirstOrDefault(t => t.FullName == typeName);

        if (type != null)
        {
            Debug.Log($"Found type: {type.FullName}");
            // Find the Baker for this type
            var bakerType = AppDomain.CurrentDomain.GetAssemblies()
                .SelectMany(a => a.GetTypes())
                .FirstOrDefault(t => t.BaseType != null && 
                                     t.BaseType.IsGenericType && 
                                     t.BaseType.GetGenericTypeDefinition() == typeof(Baker<>) &&
                                     t.BaseType.GetGenericArguments()[0] == type);

            if (bakerType != null)
            {
                Debug.Log($"Found Baker: {bakerType.FullName}");
            }
            else
            {
                Debug.Log("Baker not found.");
            }
        }
    }
}

