    {
        var holder = GameObject.Find("GodgameConfig");
        var subScene = holder.GetComponent<SubScene>();

        // Force rebuild of the entity scene
        // This is usually done by the editor loop, but we can try to trigger it.
        // One way is to dirty the subscene component or the scene asset.

        EditorUtility.SetDirty(subScene);
        AssetDatabase.ImportAsset(AssetDatabase.GetAssetPath(subScene.SceneAsset), ImportAssetOptions.ForceUpdate);

        Debug.Log("Triggered reimport/rebake");
    }
}

