using UnityEditor;
using UnityEditor.SceneManagement;
using UnityEngine;
using PureDOTS.Authoring;

public class RebakeAndClose3
{
    public static void Execute()
    {
        var comps = Resources.FindObjectsOfTypeAll<PureDotsConfigAuthoring>();
        foreach (var comp in comps)
        {
            if (comp.gameObject.scene.name == "GodgameConfig")
            {
                EditorUtility.SetDirty(comp);
                Debug.Log("Dirtied PureDotsConfigAuthoring");

                var scene = comp.gameObject.scene;
                EditorSceneManager.SaveScene(scene);
                EditorSceneManager.CloseScene(scene, true);
                Debug.Log("Saved and closed scene");
                return;
            }
        }
        Debug.LogError("Could not find PureDotsConfigAuthoring in GodgameConfig scene");
    }
}

