using UnityEngine;
using Unity.Entities;

public class CheckEntitySceneHeader2
{
    public static void Execute()
    {
        var holder = GameObject.Find("GodgameConfig");
        var subScene = holder.GetComponent<SubScene>();
        var sceneGUID = subScene.SceneGUID;

        // Try to load the entity scene header
        var headerPath = $"Assets/SceneDependencyCache/{sceneGUID}.sceneWithBuildSettings";
        var header = AssetDatabase.LoadAssetAtPath<UnityEngine.Object>(headerPath);

        if (header != null)
        {
            Debug.Log($"Found entity scene header at {headerPath}");
