        EditorUtility.SetDirty(subScene);
        AssetDatabase.ImportAsset(AssetDatabase.GetAssetPath(subScene.SceneAsset), ImportAssetOptions.ForceUpdate);

        Debug.Log("Triggered reimport/rebake");
    }
}

